class PostComments{constructor(e){this.postId=e,this.postContainer=$("#post-"+e),this.newCommentForm=$(`#post-${e}-comments-form`),this.createComment(e);let t=this;$(" .delete-comment-button",this.postContainer).each((function(){t.deleteComment($(this))}))}createComment(e){let t=this;this.newCommentForm.submit((function(o){o.preventDefault();$.ajax({type:"post",url:"/comments/create-comment",data:$(this).serialize(),success:function(o){console.log("creating comment ",o.data.comment);let n=t.newCommentDom(o.data.comment);$("#post-comments-"+e).prepend(n),t.deleteComment($(" .delete-comment-button",n)),new ToggleLike($(" .toggle-like-button",n)),new Noty({theme:"relax",text:"Comment published!",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}newCommentDom(e){return $(`<li id="comment-${e._id}">\n\n                    <div class="like-box">\n                        <a href="/likes/toggle/?id=${e._id}&type=Comment" data-likes="0" class="toggle-like-button">0 <i class="fas fa-thumbs-up like-thumbs"></i></a>\n                    </div>\n                        \n                    <div class="content">    \n                            <small>\n                                <a class="delete-comment-button" href="/comments/destroy-comment/${e._id}">X</a>\n                            </small>\n                        <p>    \n                            ${e.content}\n                            <br>\n                            <small>\n                                ${e.user.name}\n                            </small>\n                        </p> \n                    </div>   \n\n                </li>`)}deleteComment(e){$(e).click((function(t){t.preventDefault(),console.log("inside delte button "),$.ajax({type:"get",url:$(e).prop("href"),success:function(e){console.log("remove comment: ",e.data),$("#comment-"+e.data.comment_id).remove(),new Noty({theme:"relax",text:"Comment Deleted!",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}}